#!/usr/bin/env python3# -*- coding: utf-8 -*-''''''import pickledef save_object(obj,path_name):    '''    To use:        obj = name of variable        path_name = file path + name without extension    '''    try:        with open(path_name + ".pickle", "wb") as f:            pickle.dump(obj, f, protocol=pickle.HIGHEST_PROTOCOL)    except Exception as ex:        print("Error during pickling object (Possibly unsupported):", ex) def load_object(filename):    try:        with open(filename, "rb") as f:            return pickle.load(f)    except Exception as ex:        print("Error during unpickling object (Possibly unsupported):", ex) 